import{_ as i,c as l,o as a,ag as t}from"./chunks/framework.rwWslXeV.js";const f=JSON.parse('{"title":"Doelidentificatie","description":"","frontmatter":{"layout":"doc","title":"Doelidentificatie"},"headers":[],"relativePath":"tactische-syllabus/doelidentificatie.md","filePath":"tactische-syllabus/doelidentificatie.md"}'),n={name:"tactische-syllabus/doelidentificatie.md"};function r(o,e,s,c,d,h){return a(),l("div",null,e[0]||(e[0]=[t('<h1 id="doelidentificatie" tabindex="-1">Doelidentificatie <a class="header-anchor" href="#doelidentificatie" aria-label="Permalink to &quot;Doelidentificatie&quot;">​</a></h1><p>Doelidentificatie is het vermogen om vijandelijke doelen snel en accuraat te herkennen en te classificeren. Deze vaardigheid is cruciaal voor het voorkomen van friendly fire en effectief optreden.</p><h2 id="basis-principes" tabindex="-1">Basis Principes <a class="header-anchor" href="#basis-principes" aria-label="Permalink to &quot;Basis Principes&quot;">​</a></h2><h3 id="_1-visuele-identificatie" tabindex="-1">1. Visuele Identificatie <a class="header-anchor" href="#_1-visuele-identificatie" aria-label="Permalink to &quot;1. Visuele Identificatie&quot;">​</a></h3><ul><li>Herken vijandelijke uniformen</li><li>Identificeer wapens en uitrusting</li><li>Let op beweging en gedrag</li><li>Gebruik optische hulpmiddelen</li></ul><h3 id="_2-tactische-herkenning" tabindex="-1">2. Tactische Herkenning <a class="header-anchor" href="#_2-tactische-herkenning" aria-label="Permalink to &quot;2. Tactische Herkenning&quot;">​</a></h3><ul><li>Ken de vijandelijke ORBAT</li><li>Begrijp tactische patronen</li><li>Herken vijandelijke formaties</li><li>Identificeer commandostructuren</li></ul><h3 id="_3-terrein-analyse" tabindex="-1">3. Terrein Analyse <a class="header-anchor" href="#_3-terrein-analyse" aria-label="Permalink to &quot;3. Terrein Analyse&quot;">​</a></h3><ul><li>Begrijp het tactische voordeel</li><li>Identificeer waarschijnlijke posities</li><li>Herken natuurlijke dekking</li><li>Evalueer schootsvelden</li></ul><h2 id="praktische-oefeningen" tabindex="-1">Praktische Oefeningen <a class="header-anchor" href="#praktische-oefeningen" aria-label="Permalink to &quot;Praktische Oefeningen&quot;">​</a></h2><ol><li><p><strong>Quick Recognition Drill</strong></p><ul><li>Snelle identificatie van doelen</li><li>Onderscheid vriend/vijand</li><li>Prioriteer doelen</li><li>Communiceer bevindingen</li></ul></li><li><p><strong>Terrain Analysis</strong></p><ul><li>Kaart lezen</li><li>Terrein evaluatie</li><li>Positie selectie</li><li>Schootsveld analyse</li></ul></li><li><p><strong>Tactical Decision Making</strong></p><ul><li>Doel prioritering</li><li>Wapen selectie</li><li>Schietbeslissingen</li><li>Team coördinatie</li></ul></li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ul><li>Blijf altijd alert</li><li>Gebruik alle beschikbare informatie</li><li>Communiceer duidelijk</li><li>Twijfel = niet schieten</li><li>Train regelmatig</li></ul><h2 id="evaluatiecriteria" tabindex="-1">Evaluatiecriteria <a class="header-anchor" href="#evaluatiecriteria" aria-label="Permalink to &quot;Evaluatiecriteria&quot;">​</a></h2><p>Je doelidentificatie wordt beoordeeld op:</p><ul><li>Snelheid van herkenning</li><li>Nauwkeurigheid</li><li>Communicatie</li><li>Tactisch inzicht</li><li>Besluitvorming</li></ul><h2 id="veiligheid" tabindex="-1">Veiligheid <a class="header-anchor" href="#veiligheid" aria-label="Permalink to &quot;Veiligheid&quot;">​</a></h2><p>Belangrijke veiligheidsregels:</p><ol><li>Positieve identificatie vereist</li><li>Twijfel = niet schieten</li><li>Communiceer onduidelijkheden</li><li>Gebruik buddy check systeem</li><li>Volg ROE strikt op</li></ol>',19)]))}const p=i(n,[["render",r]]);export{f as __pageData,p as default};
