import{_ as i,c as a,o as n,ag as t}from"./chunks/framework.rwWslXeV.js";const g=JSON.parse('{"title":"Situational Awareness","description":"","frontmatter":{"layout":"doc","title":"Situational Awareness"},"headers":[],"relativePath":"tactische-syllabus/situational-awareness.md","filePath":"tactische-syllabus/situational-awareness.md"}'),l={name:"tactische-syllabus/situational-awareness.md"};function r(s,e,o,u,c,d){return n(),a("div",null,e[0]||(e[0]=[t('<h1 id="situational-awareness" tabindex="-1">Situational Awareness <a class="header-anchor" href="#situational-awareness" aria-label="Permalink to &quot;Situational Awareness&quot;">​</a></h1><p>Situational Awareness (SA) is het vermogen om je omgeving waar te nemen, te begrijpen en te voorspellen. Het is een cruciale vaardigheid voor elke militair.</p><h2 id="wat-is-situational-awareness" tabindex="-1">Wat is Situational Awareness? <a class="header-anchor" href="#wat-is-situational-awareness" aria-label="Permalink to &quot;Wat is Situational Awareness?&quot;">​</a></h2><p>Situational Awareness bestaat uit drie niveaus:</p><ol><li>Waarneming van elementen in de omgeving</li><li>Begrip van de huidige situatie</li><li>Voorspelling van toekomstige gebeurtenissen</li></ol><h2 id="belangrijke-elementen" tabindex="-1">Belangrijke Elementen <a class="header-anchor" href="#belangrijke-elementen" aria-label="Permalink to &quot;Belangrijke Elementen&quot;">​</a></h2><h3 id="_1-visuele-waarneming" tabindex="-1">1. Visuele Waarneming <a class="header-anchor" href="#_1-visuele-waarneming" aria-label="Permalink to &quot;1. Visuele Waarneming&quot;">​</a></h3><ul><li>Scan je omgeving systematisch</li><li>Let op beweging en patronen</li><li>Identificeer potentiële bedreigingen</li><li>Herken natuurlijke dekking</li></ul><h3 id="_2-auditieve-waarneming" tabindex="-1">2. Auditieve Waarneming <a class="header-anchor" href="#_2-auditieve-waarneming" aria-label="Permalink to &quot;2. Auditieve Waarneming&quot;">​</a></h3><ul><li>Luister naar verdachte geluiden</li><li>Identificeer de richting van geluiden</li><li>Herken verschillende wapengeluiden</li><li>Let op communicatie van de vijand</li></ul><h3 id="_3-tactische-bewustzijn" tabindex="-1">3. Tactische Bewustzijn <a class="header-anchor" href="#_3-tactische-bewustzijn" aria-label="Permalink to &quot;3. Tactische Bewustzijn&quot;">​</a></h3><ul><li>Ken je positie op de kaart</li><li>Begrijp de tactische situatie</li><li>Weet waar je kameraden zijn</li><li>Identificeer vijandelijke posities</li></ul><h2 id="praktische-oefeningen" tabindex="-1">Praktische Oefeningen <a class="header-anchor" href="#praktische-oefeningen" aria-label="Permalink to &quot;Praktische Oefeningen&quot;">​</a></h2><ol><li><p><strong>Scanning Drill</strong></p><ul><li>Oefen systematisch scannen van het terrein</li><li>Identificeer verborgen objecten</li><li>Tijd je scans voor efficiëntie</li></ul></li><li><p><strong>Sound Recognition</strong></p><ul><li>Train in het herkennen van verschillende geluiden</li><li>Oefen richtingsbepaling</li><li>Leer verschillende wapengeluiden</li></ul></li><li><p><strong>Tactical Decision Making</strong></p><ul><li>Oefen in het maken van snelle beslissingen</li><li>Train in het voorspellen van vijandelijke acties</li><li>Verbeter je reactietijd</li></ul></li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ul><li>Blijf altijd alert, zelfs in rustige situaties</li><li>Communiceer je waarnemingen met je team</li><li>Vertrouw op je instinct</li><li>Blijf trainen en oefenen</li><li>Leer van ervaringen</li></ul><h2 id="evaluatiecriteria" tabindex="-1">Evaluatiecriteria <a class="header-anchor" href="#evaluatiecriteria" aria-label="Permalink to &quot;Evaluatiecriteria&quot;">​</a></h2><p>Je SA wordt beoordeeld op:</p><ul><li>Snelheid van waarneming</li><li>Nauwkeurigheid van informatie</li><li>Effectiviteit van communicatie</li><li>Kwaliteit van besluitvorming</li><li>Algemene alertheid</li></ul>',19)]))}const m=i(l,[["render",r]]);export{g as __pageData,m as default};
